class FabrCore{fetchContent(sourceUrl){return fetch(sourceUrl).then((response)=>response.text()).then((html)=>{const parser=new DOMParser();const doc=parser.parseFromString(html,"text/html");const sourceElement=doc.querySelector("html");return sourceElement.innerHTML;}).catch((error)=>{console.error("Error:",error);});}
fetchJSON(sourceUrl){return fetch(sourceUrl).then((response)=>response.json()).then((data)=>{return data;}).catch((error)=>{console.error("Error:",error);});}}
class FabrHelper{constructor(){this.helperUID=Math.random().toString(36).substr(2,9);this.helperName="Generic";this.component=null;}
init(component){this.component=component;}}
class FabrCoreComponent extends FabrCore{constructor(){super();this.componentUID=Math.random().toString(36).substr(2,9);this.componentName="Generic";this.selector="";this.eventMap={};}
init(){this.initElements();this.initEventListeners();}
get repr(){return`<${this.componentName}:${this.componentUID}>`;}
get reprX(){return[`<${this.componentName}:${this.componentUID}>`,"background: #6c63ff; color: white; border-radius: 3px; padding: 0 3px; font-weight: bold",];}
initElements(){this.elements=document.querySelectorAll(this.selector);}
initEventListeners(){if(Object.keys(this.eventMap).length===0){this.debugger.log(`No event listeners defined for ${this.repr}on ${this.selector}`);return;}
for(let[event,fn]of Object.entries(this.eventMap)){this.elements.forEach((element)=>{element.addEventListener(event,(e)=>{this[fn](e);});});}}
addInternalEventListener(element,event,fn,reference=null){element.addEventListener(event,(e)=>{this[fn](e,reference);});}}
class Fabr extends FabrCore{constructor(){super();console.log("Fabr initializing");this.init();console.log("Fabr initialized");}
init(){this.initForms();this.initLinks();this.initCounters();this.initTooltips();this.initNotebooks();this.initTables();}
initForms(){const forms=new FabrForm();forms.init();}
initLinks(){const links=new FabrLink();links.init();}
initCounters(){const counters=new FabrCounter();counters.init();}
initTooltips(){const tooltips=new FabrTooltip();tooltips.init();}
initNotebooks(){const notebooks=new FabrNotebook();notebooks.init();}
initTables(){const tables=new FabrTable();tables.init();}}